import React, { Component } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';

const Clarifai = require('clarifai');
const API_KEY = '162638f1f65c49bcab90111f996d495c';
const MODEL_ID = 'bd367be194cf45149e75f01d59f77ba7';

const app = new Clarifai.App({
  apiKey: API_KEY,
});

export default class Home extends Component {
  state = {
    productArr: [],
  };

  getImgData = () => {

    app.models.predict(MODEL_ID, 'https://samples.clarifai.com/food.jpg').then(
      function (response) {
        this.setState({productArr: response.outputs[0].data.concepts});  
      },
      function (err) {
        console.log(err);
      }
    );
    console.log(this.state.getImgData)
  };

  render() {
    return (
      <div>
        Based on our Model, the image have the following ingrients:
        <div>
            <button onClick={()=> this.getImgData()} >Click to get data</button>
          <TableContainer className="table" component={Paper}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow className="table-top">
                  <TableCell>Product Name</TableCell>
                  <TableCell> Probability %</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {/* {this.state.list.map((row, index) => (
                  <TableRow key={index}>
                    <TableCell component="th" scope="row">
                      <img src={row.pictureUrl} alt={row.name} />
                    </TableCell>
                    <TableCell align="right">{row.name}</TableCell>
                    <TableCell align="right">
                      {row.popularity.toFixed(2)}
                    </TableCell>
                    <TableCell align="right">
                      <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => this.deleteThis(index)}
                      >
                        Delete
                      </Button>
                    </TableCell>
                  </TableRow>
                ))} */}
                <TableRow>
                  <TableCell>Potato</TableCell>
                  <TableCell>0.9</TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </div>
    );
  }
}
